import React, {useState} from 'react';
import pic from '../Images/root_landing_5.jpg';
import root from '../Images/root.jpg'
import ImgComp from "./ImgComp";
Background.propTypes = {

};

function Background(props) {
    const [x,setX] = useState(0)
    const sliderArr = [pic,root]
    const goLeft=()=>{

        x===0 ?setX(-100*(sliderArr.length-1)):setX(x+100)

    }
    const goRight=()=>{
        x===-100*(sliderArr.length-1)?setX(0):setX(x-100)
    }
    return (
        // <div className={"w-full h-screen-70"}>
        //     <img src={pic} className={"relative w-full h-screen-70 object-cover"}/>
        //     <h1 className={"relative w-full h-auto text-4xl -top-2/3 font-medium font-sans md:text-6xl text-center text-white"}>FIFTHROW SHOWCASE</h1>
        // </div>
        <div className={"w-full h-screen-70 flex relative top-background"}>

            {
                sliderArr.map((item,index)=>{
                    return (
                        <div className={'min-w-full transition-transform duration-700 ease-in-out'} key={index} style={{transform:`translateX(${x}%)`}}>
                            <ImgComp image={item}/>
                        </div>
                    )
                })
            }
            <button className={'absolute left-0 w-20 h-20 top-1/2 transform -translate-y-1/2 rounded-full bg-white opacity-80 hover:opacity-100 ' } onClick={goLeft} style={{outline:'none'}}>
                <i className="fas fa-chevron-left"></i>
            </button>
            <button className={'absolute right-0 w-20 h-20 top-1/2 transform -translate-y-1/2'} onClick={goRight} style={{outline:'none'}}>
                <i className="fas fa-chevron-right"></i>
            </button>

        </div>
    );
}

export default Background;
