import './App.css';
import Card_collection from "./Components/Card_collection";

import Logo from "./Logo.png";
import MyNav from "./Components/MyNav";
import Background from "./Components/Background";
import 'bootstrap/dist/css/bootstrap.min.css';
import React, {useState,useEffect} from "react";
import Categories from "./Components/Categories";
import Description from "./Components/Description";
import MyModal from "./Components/Modal";
import Footer from "./Components/Footer";
import arts from "./details/art.json";
import sports from "./details/sports.json";
import specialist from "./details/specialist.json";

function App() {

    const [toggle, setToggle] = useState(false);
    const [sport,setSport] = useState([])

    for (const i in sports) {
        sport.push(sports[i])
    }

    useEffect(() => {
        window.onscroll = () => {
            if (window.pageYOffset>0){
                setToggle(true)
            }else{
                setToggle(false)
            }
        }
        return ()=>{
            console.log(1)
        }
    }, []);


    return (
    <div className="App">
        <MyNav toggle={toggle}/>
        <Background/>
        <Categories/>
        <Description/>
        <Card_collection cat={sport}/>

        <Footer/>
    </div>
  );
}

export default App;
