    //         }else{
    //             setToggle(false)
    //         }
    //     }
    //     return ()=>{
    //         console.log(1)
    //     }
    // }, []);


    return (
    <div className="App">
        <MyNav/>
        <Background/>
        <Categories/>
        <Description/>
        <Card_collection change={()=>setShow(!show)} cat={sport} modal={setModal}/>
        {show && <MyModal change={setShow} detail={modal}/>}
        <Footer/>
    </div>
  );
}

export default App;
